body {
    margin: 0;
    padding: 0;
}

.preloader-container {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    box-sizing: border-box;
    overflow-x: hidden;
    overflow-y: auto;
    z-index: 10000;
    min-height: 100vh;
    background-size: cover;
    background-position: center top;
    /* background-image: url('/modules/inc/preloader_new/img/kandinskiy_preloader_bg.jpg'); */
    background-repeat: no-repeat;
